import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const SlidePage = makeShortcode("SlidePage");
const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <SlidePage mdxType="SlidePage">
      <h1>{`Elevate authoring with components`}</h1>
      <p>{`Using MDX you can use components alongside your Markdown, like React or Web Components.`}</p>
      <hr></hr>
      <button style={{
        backgroundColor: "transparent",
        color: "#FFF",
        border: "3px solid #FFF",
        borderRadius: "6px",
        padding: "1.5em 2em",
        fontSize: "1em"
      }}>Button with JSX</button>
      <hr></hr>
      <h2>{`Source of the last slide`}</h2>
      <pre><code parentName="pre" {...{
          "className": "language-jsx"
        }}>{`<button 
    style={{
        backgroundColor:"transparent", 
        color: "#FFF", 
        border: "3px solid #FFF", 
        borderRadius: "6px", 
        padding: "1.5em 2em",
        fontSize: "1em"
    }}
>Button with JSX</button>
`}</code></pre>
      <hr></hr>
      <p>{`Use other components by adding to `}<inlineCode parentName="p">{`<MDXProvider>`}</inlineCode></p>
      <pre><code parentName="pre" {...{
          "className": "language-jsx"
        }}>{`const mdComponents = {
  h1: (props) => <Heading as="h1" {...props} />,
}
`}</code></pre>
      <p><a parentName="p" {...{
          "href": "https://mdxjs.com/getting-started#mdxprovider"
        }}>{`Check out the docs here`}</a></p>
      <hr></hr>
      <h2>{`Having issues?`}</h2>
      <ul>
        <li parentName="ul"><strong parentName="li">{`Check your syntax.`}</strong>{` `}<em parentName="li">{`JSX doesn't like things like unclosed tags.`}</em>{` `}</li>
        <li parentName="ul"><strong parentName="li">{`Clear the cache and restart the server.`}</strong>{` `}<em parentName="li">{`MDX is like that sometimes.`}</em></li>
        <li parentName="ul"><strong parentName="li">{`Check the MDX docs`}</strong>{`. `}<em parentName="li">{`It's next level stuff.`}</em></li>
      </ul>
    </SlidePage>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
